// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'

export default class Main extends Component{
  state = {
    currentCards: []
  }

  createCard() {
    return {
        title: '',
        subtitle: '',
    }
  }

  addCard = () => {
    const newCard = this.createCard();
    const activeFolder = this.state.activeFolderKey
    Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
    .then(function (res) {
      const cardKey = ((Object.values(res.data))[0])
      newCard.key = cardKey
      Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
    })
    this.setState(({ currentCards }) => {
      const newArr = [
        ...currentCards,
        newCard
      ];
      return {
        currentCards: newArr
      }
    })
  }

  deleteCard = (id) => {
    console.log(id)
  }

  render() {
    return (
      <div className='Main'>
        {this.state.currentCards.map((key) => {
          return(
            <Card 
              key={key.key} 
              keyx={key.key} 
              activeFolderKey={this.props.activeFolderKey}
            />
          )
        })}
        <AddCard addCard={this.addCard} />
      </div>
    )
  }
}
