import React, {Component} from 'react'
import './CardTitle.scss'

class H1 extends Component {
    state = {
        label: 'Hello'
    }   

    onLabelChange = (e) => {
        this.setState({
            label: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.props.onItemAdded(this.state.label);
        this.setState({
            label: ''
        })
    };

    render() {
        return (
            <>
                <input type='text' placeholder='Заголовок' className='h1'/>
                <h1>{this.state.h1}</h1>
            </>
        )
    }

}

class H5 extends Component {
    state = {
        label: 'Hello'
    }   

    onLabelChange = (e) => {
        this.setState({
            label: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.props.onItemAdded(this.state.label);
        this.setState({
            label: ''
        })
    };

    render() {
        return (
            <>
                <input type='text' placeholder='Заголовок' className='h5'/>
                <h5>{this.state.h1}</h5>

            </>
        )
    }

}


export default class CardTitle extends Component {
    render() {
        return (
            <React.Fragment>
                <H1 />
                <H5 />
            </React.Fragment>
        )
    }
}
