import React, { useState, useEffect } from 'react'
import Task from '../UI/Task/Task'
import AddTask from '../UI/AddTask/AddTask'
import CardTitle from './CardTitle/CardTitle';
import Axios from '../../axios/axios-folders';
import DeleteCard from '../UI/DeleteCard/DeleteCard';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Card from '@material-ui/core/Card';


export default function CardT(props) {

  const [data, setData] = useState({ tasks: [] })

  const createTask = (name) => {
    return {
        name,
        important: false,
        done: false,
        key: ''
    }
  }

  const addTask = async (text) => {
    const newTask = createTask(text);
    const activeFolderKey = props.activeFolderKey
    const cardKey = props.keyx
    await Axios.post(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`, newTask)
    .then(function (res) {
      const taskKey = ((Object.values(res.data))[0])
      newTask.key = taskKey
      Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'key': taskKey})
    })
    setData(({ tasks }) => {
      const newArr = [
        ...tasks,
        newTask
      ];
      return {
        tasks: newArr
      }
    })
  }

  const deleteTask = (name, key) => {
    const idx = data.tasks.findIndex((el) => el.name === name)
    const activeFolderKey = props.activeFolderKey
    const cardKey = props.keyx
    Axios.delete(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${key}.json`)
    setData(({ tasks }) => {
      const newArr = [
        ...tasks.slice(0, idx),
        ...tasks.slice(idx + 1)
      ]
      return {
        tasks: newArr
      }
    })
  }

  const toggleProperty = (arr, name, propName) => {
    const idx = arr.findIndex((el) => el.name === name);

    const oldItem = arr[idx];
    const newItem = {...oldItem,
      [propName]: !oldItem[propName]}

    return [
      ...arr.slice(0, idx),
      newItem,
      ...arr.slice(idx + 1)
    ]
  }

  const onToggleImportant = (name, key) => {
    const idx = data.tasks.findIndex((el) => el.name === name);
    const oldItem = data.tasks[idx];
    const newItem = {...oldItem,
      ['important']: !oldItem['important']
    }
    const activeFolderKey = props.activeFolderKey
    const cardKey = props.keyx
    const taskKey = key
    Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'important': newItem.important})
    setData(({ tasks }) => {
      return {
        tasks: toggleProperty(tasks, name, 'important')
      }
    })
  }

  const onComplete = (name, key) => {
    const idx = data.tasks.findIndex((el) => el.name === name);
    const oldItem = data.tasks[idx];
    const newItem = {...oldItem,
      ['done']: !oldItem['done']
    }
    const activeFolderKey = props.activeFolderKey
    const cardKey = props.keyx
    const taskKey = key
    Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'done': newItem.done})
    setData(({ tasks }) => {
      return {
        tasks: toggleProperty(tasks, name, 'done')
      }
    })
  }

  useEffect(() => {
    const fetchData = async () => {
      try {  
        const cardKey = props.keyx
        const activeFolderKey = props.activeFolderKey
        const responseTasks = await Axios.get(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`)
        const tasks = []
        Object.values(responseTasks.data).forEach((task) => {
          tasks.push(task)
        })  
        setData({
          tasks
        }) 
      } catch (e) {
        console.log(e)
      }  
    }
    fetchData()
  }, [])

  return (
    <Card style={{ minHeight: '83vh' }}>
      <CardHeader
        title={<CardTitle />}
        action={
          <DeleteCard 
            cardKey={props.id}  
            deleteCard={props.onDeleteCard}
            keyx={props.keyx}
          />
        }
      />
      <CardContent>
        {data.tasks.map((task) => {
          return( 
            <Task
              name={task.name}
              important={task.important}
              done={task.done}
              key={task.key}
              keyx={task.key}
              onToggleImportant={onToggleImportant}
              onDeleted={deleteTask}
              onComplete={onComplete}
            />   
          )                 
        })}
        <AddTask text='Добавить задачу' onItemAdded={addTask} />
      </CardContent>
    </Card> 
  )
}
