import React from 'react'
import './Checkbox.scss'

const Checkbox = props => {
    function readyTask() {
        Input.style.color = 'red'
    }
    return (
        <div>
            <input 
                className='checkbox-custom' 
                type="checkbox" 
                id={props.id}
                onClick={readyTask}
            />
            <label for={props.id}>{props.task}</label>
        </div>
    )
}

export default Checkbox
