// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'

export default class Main extends Component{
  state = {
    currentCards: []
  }

  createCard() {
    return {
        title: '',
        subtitle: '',
    }
  }

  addCard = () => {
    const activeFolder = this.props.folderId
    if (activeFolder !== undefined) {
      const newCard = this.createCard();
      Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
      .then(function (res) {
        const cardKey = ((Object.values(res.data))[0])
        newCard.key = cardKey
        Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
      })
      this.setState(({ currentCards }) => {
        const newArr = [
          ...currentCards,
          newCard
        ];
        return {
          currentCards: newArr
        }
      })  
    } else {
      console.log('Not today')
    }
  }

  deleteCard = (id) => {
    console.log(id)
  }

  async componentDidMount() {
    try {  
      const activeFolder = this.props.folderId    
      const responseCards = await Axios.get(`/folders/${activeFolder}/cards.json`)
      const currentCards = []
      Object.values(responseCards.data).forEach((card) => {
        currentCards.push(card)
      })  

      this.setState({
        currentCards
      }) 

    } catch (e) {
      console.log(e)
    }
  }

  async componentDidMount() {
    try {  
      const activeFolder = this.props.folderId    
      const responseCards = await Axios.get(`/folders/${activeFolder}/cards.json`)
      const currentCards = []
      Object.values(responseCards.data).forEach((card) => {
        currentCards.push(card)
      })  

      this.setState({
        currentCards
      }) 

    } catch (e) {
      console.log(e)
    }
  }

  render() {
    return (
      <div className='Main'>
        {this.state.currentCards.map((card) => {
          return(
            <Card 
              key={card.key} 
              keyx={card.key} 
              activeFolderKey={this.props.folderId}
              title={card.title}
            />
          )
        })}
        <AddCard addCard={this.addCard} />
      </div>
    )
  }
}
