// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import AddTask from '../../components/UI/AddTask/AddTask'
import Axios from '../../axios/axios-folders'
import MailIcon from '@material-ui/icons/Mail';
import AddIcon from '@material-ui/icons/Add';
import FolderIcon from '@material-ui/icons/Folder';
import CreateNewFolderIcon from '@material-ui/icons/CreateNewFolder';
import { ListItem, ListItemIcon, ListItemText, Divider } from '@material-ui/core/'
import { Link } from 'react-router-dom'



export default class Menu extends Component {
  state = {
    folders: [],
  }

  createFolder(name) {
    return {
      name
    }
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  addFolder = async (text) => {
    console.log('Folder 1')
    const newFolder = this.createFolder(text);
    await Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      console.log('Folder 2')
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    this.setState(({ folders }) => {
      console.log('Folder 3')
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })
  }

  async componentDidMount() {
    try {      
      const responseFolders = await Axios.get('/folders.json')
      const folders = []
      Object.values(responseFolders.data).forEach((folder) => {
        folders.push(folder)
      })  
      this.setState({
        folders
      }) 
    } catch (e) {
      console.log(e)
    }
  }

  render() {
    return (
      <>
        {this.state.folders.map((item) => (
          <Link to={`/${item.key}`}>
            <ListItem button key={item.key}>
              <ListItemIcon><FolderIcon /></ListItemIcon>
              <ListItemText primary={item.name} />
            </ListItem>
          </Link>
        ))}
        <Divider />
        <ListItem>
          <ListItemIcon><CreateNewFolderIcon /></ListItemIcon>
          <ListItemText primary={
            <AddTask 
              text='Новая папка' 
              onItemAdded={this.addFolder}
            />       
          }/>
        </ListItem>

      </>
    )    
  }
}
