            }
        })
    };

    onSelectFolder = (key) => {
      console.log(key)
    }

    async componentDidMount() {
      try {
        const response = await Axios.get('/folders.json')
        const folders = []

        Object.values(response.data).forEach((folder) => {
          folders.push(folder)
        })

        this.setState({
          folders
        })

      } catch (e) {
        console.log(e)
      }
    }


    render() {
        return (
            <div className='Menu'>
                <ul className="list-group">
                    {
                        this.state.folders.map((item) => {
                            return(
                                <Folder 
                                    title={item.name} 
                                    id={item.id} 
                                    key={item.key}          
                                    onDelete={this.deleteItem}
                                    onSelectFolder={this.onSelectFolder}
                                />
                            )
                        })
                    }
                    <div className='addTask'>
                        <AddTask 
                            text='Новая папка' 
                            onItemAdded={this.addFolder}
                        />
                    </div>
                </ul>
            </div>
        )    
    }
}
