import React, { Component } from 'react';
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from './axios/axios-folders'


export default class App extends Component { 
  state = {
    folders: [],
    currentCards: [],
    activeFolderKey: ''
	}









  deleteCard = (id) => {
    console.log(id)
  }



    deleteFolder = (id) => {
        this.setState(({ folders }) => {
            const idx = folders.findIndex((el) => el.id === id);
            const newArray = [
                ...folders.slice(0, idx),
                ...folders.slice(idx + 1)
            ];
            return {
                folders: newArray
            }
        })
    };

    onSelectFolder = async (key) => {
      this.setState({
        currentCards: []
      })
      this.setState({
        activeFolderKey: key
      })
      try {
        const response = await Axios.get(`/folders/${this.state.activeFolderKey}/cards.json`)
        console.log(response)
        const currentCards = []
        Object.values(response.data).forEach((card) => {
          currentCards.push(card)
        })

        this.setState({
            currentCards
        })

    } catch (e) {
        console.log(e)
  }}

  onToggleImportant = (id) => {
    this.setState(({ items }) => {
        return {
            items: this.toggleProperty(items, id, 'important')
        }
    });
};

createTask(name) {
  return {
      name,
      important: false,
      done: false,
  }
}

addTask = (text) => {
  const newItem = this.createTask(text);
  this.setState(({ items }) => {
      const newArr = [
          ...items,
          newItem
      ];
      return {
          items: newArr
      }
  })
  Axios.post(`/folders/${activeFolder}/cards/${cardKey}/tasks.json`, newItem)
};


deleteTask = (id) => {
  this.setState(({ items }) => {
      const idx = items.findIndex((el) => el.id === id);
      const newArr = [
          ...items.slice(0, idx),
          ...items.slice(idx + 1)
      ];
      return {
          items: newArr
      }
  })
};

toggleProperty(arr, id, propName) {
  const idx = arr.findIndex((el) => el.id === id);

  const oldItem = arr[idx];
  const newItem = {...oldItem,
      [propName]: !oldItem[propName]};

  return [
      ...arr.slice(0, idx),
      newItem,
      ...arr.slice(idx + 1)
  ];
}


    async componentDidMount() {
      try {
        const response = await Axios.get('/folders.json')
        const folders = []

        Object.values(response.data).forEach((folder) => {
          folders.push(folder)
        })

        this.setState({
          folders
        })

      } catch (e) {
        console.log(e)
      }
    }

	render() {
		return (
			<div className="App">
				<div className='row'>
					<div className='col-2'>
            <Menu 
              folders={this.state.folders} 
              addFolder={this.addFolder}
              deleteFolder={this.deleteFolder}
              onSelectFolder={this.onSelectFolder}
              addTask={this.addTask}
              deleteTask={this.deleteTask}
              onToggleImportant={this.onToggleImportant}

            />
					</div>
					<div className='col-10'>
						<Main 
              addCard={this.addCard}
              cards={this.state.currentCards}
              deleteCard={this.deleteCard}
            />
					</div>
				</div>
			</div>
		);
	}
}
