import React from 'react'
import Badge from '@material-ui/core/Badge';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import { Link } from 'react-router-dom'
import FolderIcon from '@material-ui/icons/Folder';
import { ListItem, ListItemIcon, ListItemText, Divider } from '@material-ui/core/'


export default function Folder(props) {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return(
    <>
      <ListItem button key={props.folderID} >
        <Link to={`/folders/${props.folderID}`}>
          <ListItemIcon>
            <Badge badgeContent={1} color="primary">
              <FolderIcon />
            </Badge>
          </ListItemIcon>
        </Link>
        <Link to={`/folders/${props.folderID}`}>
          <ListItemText primary={props.name} />
        </Link>
        <IconButton aria-controls={props.folderID} aria-haspopup="true" onClick={props.handleClick}>
          <MoreHorizIcon />
        </IconButton>
      </ListItem>
      <Menu
        id={props.folderID}
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem onClick={props.handleClose}>Переименовать</MenuItem>
        <MenuItem onClick={() => props.deleteFolder(props.folderID)}>Удалить</MenuItem>
      </Menu>
    </> 
  )
}
