import React, {Component} from 'react'
import Checkbox from '../../components/Checkbox/Checkbox'
import AddTask from '../../components/AddTask/AddTask'
import './TaskElement.scss'

export default class TaskElement extends Component{
    state = {
        items: [
            { id: 1, value: '', important: false, done: false},
            { id: 2, value: 'World', important: true, done: false},     
            { id: 3, value: 'Kak', important: false, done: false},
            { id: 4, value: 'Dela', important: false, done: false},        
        ]
    }

    render() {
        return (
            <div className='TaskElement'>
                <input type='text' placeholder='Заголовок' className='h1' />
                <input type='text' placeholder='Примечание' className='h5' />
                {this.state.items.map((item) => {
                    return( 
                        <Checkbox
                            value={item.value}
                            id={item.id}
                            important={item.important}
                            done={item.done}
                        />
                    )
                })}
                <AddTask text='Добавить новую задачу' />
            </div>
        )

    }
}
