import React, {Component} from 'react'
import './CardTitle.scss'

export default class CardTitle extends Component {
    state = {
        h1: '',
        h5: ''
    }

    addItem = (text) => {
        const newItem = this.createTodoItem(text);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newItem
            ];

            return {
                items: newArr
            }
        })
    };

    onLabelChange = (e) => {
        this.setState({
            h1: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.props.add(this.state.h1);
        this.setState({
            h1: ''
        })
    };

    render() {
        return (
            <React.Fragment>
                <form
                    onSubmit={this.onSubmit}
                >
                    <button className="btn" type="submit" id="button-addon1" />
                    <input 
                        type='text' 
                        placeholder='Заголовок' 
                        className='h1'
                        onChange={this.onLabelChange}    
                    />
                    <input 
                        type='text' 
                        placeholder='Заголовок' 
                        className='h5'
                        aria-describedby="button-addon1" 
                    />
                </form>
            </React.Fragment>
        )
    }
}
