import React, {Component} from 'react'
import Checkbox from '../../components/Checkbox/Checkbox'
import AddTask from '../../components/AddTask/AddTask'
import './TaskElement.scss'
import CardTitle from '../CardTitle/CardTitle';
import Axios from 'axios';

export default class TaskElement extends Component{
    maxId = 100;

    state = {
        items: [
            this.createTodoItem('Hello World!')
        ]
    }

    createTodoItem(value) {
        return {
            value,
            important: false,
            done: false,
            id: this.maxId++
        }
    }

    deleteItem = (id) => {
        this.setState(({ items }) => {
            const idx = items.findIndex((el) => el.id === id);
            const newArray = [
                ...items.slice(0, idx),
                ...items.slice(idx + 1)
            ];
            return {
                items: newArray
            }
        })
    };

    toggleProperty(arr, id, propName) {
        const idx = arr.findIndex((el) => el.id === id);

        const oldItem = arr[idx];
        const newItem = {...oldItem,
            [propName]: !oldItem[propName]};

        return [
            ...arr.slice(0, idx),
            newItem,
            ...arr.slice(idx + 1)
        ];
    }

    addItem = (text) => {
        const newItem = this.createTodoItem(text);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newItem
            ];

            return {
                items: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/test.json', newItem)
    };

    // async componentDidMount() {
    //     try {
    //         const response = await Axios.get('https://tasks-8f93f.firebaseio.com/test.json')
    //         const items = []
    //         Object.keys(response.data).forEach(async (key) => {
    //             const res = await Axios.get(`https://tasks-8f93f.firebaseio.com/test/${key}.json`)
    //             items.push(this.createTodoItem(res.data.value))
    //         })
    //         this.setState({
    //             items
    //         })
    //     } catch(e) {
    //         console.log(e)
    //     }
    // }

    onToggleImportant = (id) => {
        this.setState(({ items }) => {
            return {
                items: this.toggleProperty(items, id, 'important')
            }
        });
    };

    renderTasks = () => {
        return this.state.items.map((item) => {
            return( 
                <Checkbox
                    value={item.value}
                    id={item.id}
                    important={item.important}
                    done={item.done}
                    onToggleImportant={this.onToggleImportant}
                    onDeleted={ this.deleteItem }
                />
            )
        })
    }

    render() {
        return (
            <div className='TaskElement'>
                <CardTitle />
                {this.renderTasks}
                <AddTask text='Добавить новую задачу' onItemAdded={this.addItem} />
            </div>
        )

    }
}
