// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/AddCard/AddCard'
import Axios from 'axios'

export default class Main extends Component{
    maxId = 1000

    state = {
        cards: [],
        keys: []
    }

    createCard() {
        return {
            title: '',
            subtitle: '',
            id: this.maxId++,
        }
    }

    addCard = () => {
        const newCard = this.createCard();
        this.setState(({ cards }) => {
            const newArr = [
                ...cards,
                newCard
            ];
            return {
                cards: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/cards.json', newCard)
    };

    async componentDidMount() {
        try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/cards.json')
            const cards = []
            const keys = []

            Object.keys(response.data).forEach((key) => {
                cards.push(this.createCard())
                keys.push(this.createCard())
            })

            this.setState({
                cards
            })

        } catch (e) {
            console.log(e)
        }
    }  


    render() {
        return (
            <div className='Main'>
                {this.state.cards.map((card) => {
                    return(
                        <Card name={card.name} />
                    )
                })

                }
                <AddCard addCard={this.addCard} />
            </div>
        )
    }
}
