// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import TaskElement from '../../components/TaskElement/TaskElement'
import AddTaskElement from '../../components/AddTaskElement/AddTaskElement'
import Axios from 'axios'

export default class Main extends Component{
    maxId = 1000

    state = {
        cards: []
    }

    createTodoItem(name) {
        return {
            name,
            important: false,
            done: false,
            id: this.maxId++
        }
    }

    addItem = (text) => {
        const newItem = this.createTodoItem(text);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newItem
            ];

            return {
                items: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/test.json', newItem)
    };


    render() {
        return (
            <div className='Main'>
                <TaskElement />
                <AddTaskElement  />
            </div>
        )
    }
}
