// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/AddCard/AddCard'
import Axios from 'axios'

export default class Main extends Component{
    maxId = 1000

    state = {
        cards: [],
        keys: []
    }

    createCard() {
        return {
            title: '',
            subtitle: '',
            id: this.maxId++,
        }
    }

    addCard = () => {
        const newCard = this.createCard();
        this.setState(({ cards }) => {
            const newArr = [
                ...cards,
                newCard
            ];
            return {
                cards: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/cards.json', newCard)
        // this.addCardToState()
    };

    // addCardToState = async () => {
    //     const response = await Axios.get('https://tasks-8f93f.firebaseio.com/cards.json')
    //     const lastKey = Object.keys(response.data)[Object.keys(response.data).length - 1]
    //     this.setState(({ keys }) => {
    //         const newArr = [
    //             ...keys,
    //             lastKey
    //         ];
    //         return {
    //             keys: newArr
    //         }
    //     })
    // }

    async componentDidMount() {
        try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/cards.json')
            const cards = []
            const keys = []
            Object.keys(response.data).forEach((key) => {
                cards.push(this.createCard())
                keys.push(key)
            })

            this.setState({
                cards
            })

            this.setState({
                keys
            })
        } catch (e) {
            console.log(e)
        }
    }

    renderCards = () => {
        for(let i = 0; i < this.state.cards.length - 1; i++) {
            console.log(this.state.keys)
            return <Card name={this.state.keys[i]} />
        }
    }


    render() {
        return (
            <div className='Main'>
                {this.renderCards()}
                <AddCard addCard={this.addCard} />
            </div>
        )
    }
}
