// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import './Menu.scss'
import AddTask from '../../components/AddTask/AddTask'
import MenuItem from '../../components/MenuItem/MenuItem'

export default class Menu extends Component {
    maxId = 100

    state = {
        folders: [
            this.createFolder('Main Folder'),
            this.createFolder('Second Folder')
        ]
    }

    createFolder(value) {
        return {
            value,
            id: this.maxId++
        }
    }

    render() {
        return (
            <div className='Menu'>
                <ul class="list-group">
                    {
                        this.state.folders.map((item) => {
                            return(
                                <MenuItem title={this.state.value} />

                            )
                        })
                    }
                    <AddTask text='Новая папка' />
                </ul>
            </div>
        )    
    }
}
