// Контейнер для отображения элементов карточек с задачами
import React, { useState, useEffect } from 'react'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'
import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';

export default function Main(props) {

  const [data, setData] = useState({ currentCards: [] })
  const [loading, setLoading] = useState(true)

  const createCard = () => {
    return {
      title: '',
      subtitle: '',
    }
  }

  const addCard = async () => {
    const activeFolder = props.folderId
    const newCard = createCard();
    await Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
    .then(function (res) {
      const cardKey = ((Object.values(res.data))[0])
      newCard.key = cardKey
      Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
    })
    setData(({ currentCards }) => {
      const newArr = [
        ...currentCards,
        newCard
      ];
      return {
        currentCards: newArr
      }
    })  
  }

  const onDeleteCard = (id, keyx) => {
    const idx = data.currentCards.findIndex((el) => el.id === id)
    const activeFolderKey = props.folderId
    Axios.delete(`/folders/${activeFolderKey}/cards/${keyx}.json`)
    this.setData(({ currentCards }) => {
      const newArr = [
        ...currentCards.slice(0, idx),
        ...currentCards.slice(idx + 1)
      ]
      return {
        currentCards: newArr
      }
    })
  }

  useEffect(async () => {
    const fetchData = async () => {
      try {  
        const activeFolder = props.folderId    
        const responseCards = await Axios.get(`/folders/${activeFolder}/cards.json`)
        const currentCards = []
        Object.values(responseCards.data).forEach((card) => {
          currentCards.push(card)
        })  
        setData({
          currentCards
        }) 
      } catch (e) {
        console.log(e)
      }
    }
    fetchData()
    setLoading(false)
  }, [])

  return (
    <Box display="flex" flexDirection="row" flexWrap="nowrap" p={1} overflow='scroll'>
      {loading ?
      <CircularProgress /> :
      <>{data.currentCards.map((card) => {
        return(
          <Box p={1} minWidth='23%' maxWidth='23%'>
            <Card 
              key={card.key} 
              keyx={card.key} 
              activeFolderKey={props.folderId}
              title={card.title}
              onDeleteCard={onDeleteCard}
              id={card.id}
            />
          </Box>
        )
      })}
      <Box p={1} minWidth='23%' maxWidth='23%'>
        <AddCard addCard={addCard} />
      </Box>
      </>}
    </Box>
  )
}
