// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import './Menu.scss'
import AddTask from '../../components/UI/AddTask/AddTask'
import Folder from '../../components/UI/Folder/Folder'
import Axios from '../../axios/axios-folders'

export default class Menu extends Component {
  state = {
    folders: [],
  }

  //

  createFolder(name) {
    return {
      name
    }
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  updateState = async ()

  addFolder = (text) => {
    console.log(`Add Folder: ${this.state}`)
    const newFolder = this.createFolder(text);
    Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    this.setState(({ folders }) => {
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })

  }

  // async componentDidUpdate() {
  //   try {      
  //     console.log('componentDidUpdate')
  //     const responseFolders = await Axios.get('/folders.json')
  //     const folders = []
  //     Object.values(responseFolders.data).forEach((folder) => {
  //       folders.push(folder)
  //     })  
  //     this.setState({
  //       folders
  //     }) 
  //   } catch (e) {
  //     console.log(e)
  //   }
  // }

  async componentDidMount() {
    try {      
      console.log(`componentDidMount: ${this.state}`)
      const responseFolders = await Axios.get('/folders.json')
      const folders = []
      Object.values(responseFolders.data).forEach((folder) => {
        folders.push(folder)
      })  
      this.setState({
        folders
      }) 
    } catch (e) {
      console.log(e)
    }
  }

  render() {
    return (
      <div className='Menu'>
        <ul className="list-group">
          {
            this.state.folders.map((item) => {
              return(
                <Folder 
                  title={item.name} 
                  keyx={item.key}  
                  key={item.key}        
                  onDelete={this.deleteItem}
                />
              )
            })
          }
          <div className='addTask'>
            <AddTask 
              text='Новая папка' 
              onItemAdded={this.addFolder}
            />
          </div>
        </ul>
      </div>
    )    
  }
}
