// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'

export default class Main extends Component{

    state = {
        cards: [],
    }



    createCard() {
        return {
            title: '',
            subtitle: '',
        }
    }

    addCard = () => {
        const newCard = this.createCard();
        const activeFolder = this.props.activeFolderKey
        console.log(activeFolder)
        Axios.post(`/folders/${this.props.activeFolderKey}/cards.json`, newCard)
        .then(function (res) {
          const cardKey = ((Object.values(res.data))[0])
          newCard.key = cardKey
          Axios.patch(`/folders/${this.props.activeFolderKey}/cards/${cardKey}.json`, {'key': cardKey})
        })
        this.setState(({ cards }) => {
            const newArr = [
                ...cards,
                newCard
            ];
            return {
                cards: newArr
            }
        })
    };

    async componentDidMount() {
        try {
            const response = await Axios.get(`/folders/${this.props.activeFolderKey}/cards.json`)
            console.log(response)
            const cards = []
            Object.values(response.data).forEach((card) => {
              cards.push(card)
            })

            this.setState({
                cards
            })

        } catch (e) {
            console.log(e)
        }
    }

    render() {
        return (
            <div className='Main'>
                {this.state.cards.map((key) => {
                    return(
                        <Card name={key} />
                    )
                })}
                <AddCard addCard={this.addCard} />
            </div>
        )
    }
}
