// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import Box from '@material-ui/core/Box';
import { sizing } from '@material-ui/system'


export default class Main extends Component{
  maxId = 1

  state = {
    currentCards: []
  }

  createCard() {
    return {
        title: '',
        subtitle: '',
        id: this.maxId++
    }
  }

  addCard = async () => {
    console.log('Card 1')
    const activeFolder = this.props.folderId
    const newCard = this.createCard();
    await Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
    .then(function (res) {
      console.log('Card 2')
      const cardKey = ((Object.values(res.data))[0])
      newCard.key = cardKey
      Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
    })
    this.setState(({ currentCards }) => {
      console.log('Card 3')
      const newArr = [
        ...currentCards,
        newCard
      ];
      return {
        currentCards: newArr
      }
    })  
  }

  onDeleteCard = (id, keyx) => {
    const idx = this.state.currentCards.findIndex((el) => el.id === id)
    const activeFolderKey = this.props.folderId
    Axios.delete(`/folders/${activeFolderKey}/cards/${keyx}.json`)
    this.setState(({ currentCards }) => {
      const newArr = [
        ...currentCards.slice(0, idx),
        ...currentCards.slice(idx + 1)
      ]
      return {
        currentCards: newArr
      }
    })
  }

  async componentDidMount() {
    try {  
      const activeFolder = this.props.folderId    
      const responseCards = await Axios.get(`/folders/${activeFolder}/cards.json`)
      const currentCards = []
      Object.values(responseCards.data).forEach((card) => {
        currentCards.push(card)
      })  
      this.setState({
        currentCards
      }) 

    } catch (e) {
      console.log(e)
    }
  }

  render() {
    return (
      <>
        <Grid
          container
          justify="flex-start"
          spacing={2}
          wrap='nowrap'
        >
          {this.state.currentCards.map((card) => {
            return(
              <>
                <Grid item xs={3}>
                  <Card 
                    key={card.key} 
                    keyx={card.key} 
                    activeFolderKey={this.props.folderId}
                    title={card.title}
                    onDeleteCard={this.onDeleteCard}
                    id={card.id}
                  />
                </Grid>
              </>
            )
          })}
          <Grid item xs={3}>
            <AddCard addCard={this.addCard} />
          </Grid>
        </Grid>

      </>
    )
  }
}

<Box display="flex" p={1} bgcolor="background.paper">
  <Box p={1} order={2} bgcolor="grey.300">
    Item 1
  </Box>
  <Box p={1} order={3} bgcolor="grey.300">
    Item 2
  </Box>
  <Box p={1} order={1} bgcolor="grey.300">
    Item 3
  </Box>
</Box>
