import React, {Component} from 'react'
import Checkbox from '../../components/Checkbox/Checkbox'
import AddTask from '../../components/AddTask/AddTask'
import './Main.scss'

export default class Main extends Component{
    maxId = 5
    state = {
        items: [
            {
                title: 'Today',
                item: [
                    { id: 1, value: 'Privet', important: false, done: false},
                    { id: 2, value: 'World', important: true, done: false},        
                ]
            },
            {
                title: 'Tomorrow',
                item: [
                    { id: 3, value: 'Kak', important: false, done: false},
                    { id: 4, value: 'Dela', important: false, done: false},        
                ]
            }
        ]
    }

    createItem(title, value) {
        return {
            title,
            item: {}
        }
    }

    render() {
        return (
            <div className='Main'>
                {this.state.items.map((item) => {
                    return (
                        <React.Fragment>
                            <h1>{item.title}</h1>
                            {item.item.map((item) => {
                                return( 
                                    <Checkbox
                                        value={item.value}
                                        id={item.id}
                                        important={item.important}
                                        done={item.done}
                                    />
                                )
                            })}
                            <AddTask />
                        </React.Fragment>
                    )
                })}
            </div>
        )

    }
}
