import React, {Component} from 'react'
import './CardTitle.scss'

export default class CardTitle extends Component {
    state = {
        h1: '',
        h5: ''
    }
    onLabelChange = (e) => {
        this.setState({
            label: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault();
        this.props.onItemAdded(this.state.label);
        this.setState({
            label: ''
        })
    };
    render() {
        return (
            <React.Fragment>
                <form>
                    <input type='text' placeholder='Заголовок' className='h1'/>
                    <input type='text' placeholder='Заголовок' className='h5'/>
                </form>
            </React.Fragment>
        )
    }
}
