import React, {Component} from 'react'
import Checkbox from '../UI/Task/Task'
import AddTask from '../UI/AddTask/AddTask'
import './Card.scss'
import CardTitle from './CardTitle/CardTitle';
import Axios from 'axios';

export default class Card extends Component {

    state = {
        items: []
    }


    async componentDidMount() {
        try {
            const response = await Axios.get(`https://tasks-8f93f.firebaseio.com/cards/${this.props.name}/tasks.json`)
            const items = []

            Object.values(response.data).forEach((task) => {
                items.push(this.createTask(task.name))
            })
            this.setState({
                items
            })

        } catch (e) {
            console.log(e)
        }
    }  



    render() {
        return (
            <div className='TaskElement'>
                <CardTitle />
                {this.state.items.map((item) => {
                    return( 
                        <Checkbox
                            name={item.name}
                            id={item.id}
                            important={item.important}
                            done={item.done}
                            onToggleImportant={this.onToggleImportant}
                            onDeleted={ this.deleteTask }
                        />   
                    )                 
                })}
                <AddTask text='Добавить новую задачу' onItemAdded={this.addTask} />
            </div>
        )
    }
}
