// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import './Menu.scss'
import AddTask from '../../components/AddTask/AddTask'
import Folder from '../../components/Folder/Folder'
import Axios from 'axios'

export default class Menu extends Component {

    addFolder = (text) => {
        const newFolder = this.createFolder(text);
        this.setState(({ folders }) => {
            const newArr = [
                ...folders,
                newFolder
            ];
            return {
                folders: newArr
            }
        })
        Axios.post(`https://tasks-8f93f.firebaseio.com/folders.json`, newFolder)
    };

    deleteItem = (id) => {
        this.setState(({ folders }) => {
            const idx = folders.findIndex((el) => el.id === id);
            const newArray = [
                ...folders.slice(0, idx),
                ...folders.slice(idx + 1)
            ];
            return {
                folders: newArray
            }
        })
    };

    async componentDidMount() {
        try {
            const response = await Axios.get(`https://tasks-8f93f.firebaseio.com/folders.json`)
            const folders = []

            Object.values(response.data).forEach((folder) => {
                folders.push(this.createFolder(folder.name))
            })
            this.setState({
                folders
            })

        } catch (e) {
            console.log(e)
        }
    }  

    render() {
        return (
            <div className='Menu'>
                <ul className="list-group">
                    {
                        this.state.folders.map((item) => {
                            return(
                                <Folder 
                                    title={item.name} 
                                    id={item.id}           
                                    onDelete={this.deleteItem}
                                />
                            )
                        })
                    }
                    <div className='addTask'>
                        <AddTask 
                            text='Новая папка' 
                            onItemAdded={this.addFolder}
                        />
                    </div>
                </ul>
            </div>
        )    
    }
}
