import React, {Component} from 'react'
import './Checkbox.scss'


class Checkbox extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            styl : { color: 'green' }
        }
    }
    render() {
        const styl = this.state.styl
        return (
            <div>
                <input 
                    className='checkbox-custom' 
                    type="checkbox" 
                    id={this.props.id}
                />
                <label 
                    for={this.props.id} 
                    style={ styl } 
                >{this.props.task}</label>
            </div>
        )

    }
}

export default Checkbox
