// Контейнер для панели коллекции папок
import React, { useState, useEffect } from 'react'
import Axios from '../../axios/axios-folders'
import FolderIcon from '@material-ui/icons/Folder';
import CreateNewFolderIcon from '@material-ui/icons/CreateNewFolder';
import { ListItem, ListItemIcon, ListItemText, Divider } from '@material-ui/core/'
import { Link } from 'react-router-dom'
import AddFolder from '../../components/UI/AddFolder/AddFolder'
import Badge from '@material-ui/core/Badge';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';

export default function MenuT() {
  const [anchorEl, setAnchorEl] = useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const [data, setData] = useState({ folders: [] })

  const createFolder = (name) => {
    return {
      name
    }
  }

  const deleteFolder = (key) => {
    Axios.delete(`/folders/${key}.json`)
    setData((folders) => {
      const idx = folders.findIndex((key) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  const addFolder = async (text) => {
    const newFolder = createFolder(text);
    await Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    setData(({ folders }) => {
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })
  }

  useEffect(() => {
    const fetchData = async () => {
      try {      
        const responseFolders = await Axios.get('/folders.json')
        const folders = []
        Object.values(responseFolders.data).forEach((folder) => {
          folders.push(folder)
        })  
        setData({
          folders
        }) 
      } catch (e) {
        console.log(e)
      }  
    }
    fetchData()
  }, [])


  return(
    <>
      {data.folders.map((item) => (
        <>
          <ListItem button key={item.key} onClick={handleClick}>
            <Link to={`/folders/${item.key}`}>
              <ListItemIcon>
                <Badge badgeContent={1} color="primary">
                  <FolderIcon />
                </Badge>
              </ListItemIcon>
            </Link>
            <Link to={`/folders/${item.key}`}>
              <ListItemText primary={item.name} />
            </Link>
            <IconButton aria-controls={item.name} aria-haspopup="true" onClick={handleClick}>
              <MoreHorizIcon />
            </IconButton>
          </ListItem>
          <Menu
            id={item.name}
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem onClick={handleClose}>Переименовать</MenuItem>
            <MenuItem onClick={deleteFolder(item.key)}>Удалить</MenuItem>
          </Menu>
        </> 
      ))}
      <Divider />
      <ListItem>
        <ListItemIcon><CreateNewFolderIcon /></ListItemIcon>
        <ListItemText primary={
          <AddFolder 
            text='Новая папка' 
            onItemAdded={addFolder}
          />       
        }/>
      </ListItem>
    </>
  )     
}
