import React from 'react'
import {styles} from'./Checkbox.scss'


const Checkbox = props => {
    let textStyle = ''

    function readyTask() {
        textStyle = "color:'red'"
    }
    return (
        <div>
            <input 
                className='checkbox-custom' 
                type="checkbox" 
                id={props.id}
                onClick={readyTask}
            />
            <label for={props.id} style={{color: 'red'}}>{props.task}</label>
        </div>
    )
}

export default Checkbox
