import React, { Component } from 'react';
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from './axios/axios-folders'


export default class App extends Component { 
  state = {
    folders: [],
    activeFolderKey
	}

	createFolder(name) {
    return {
      name,
    }
  }

    addFolder = (text) => {
        const newFolder = this.createFolder(text);
        Axios.post(`/folders.json`, newFolder)
        .then(function (res) {
          const folderKey = ((Object.values(res.data))[0])
          newFolder.key = folderKey
          Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
        })

        this.setState(({ folders }) => {
            const newArr = [
                ...folders,
                newFolder
            ];
            return {
                folders: newArr
            }
        })
    };

    deleteFolder = (id) => {
        this.setState(({ folders }) => {
            const idx = folders.findIndex((el) => el.id === id);
            const newArray = [
                ...folders.slice(0, idx),
                ...folders.slice(idx + 1)
            ];
            return {
                folders: newArray
            }
        })
    };

    onSelectFolder = (key) => {
      console.log(key)
    }

    async componentDidMount() {
      try {
        const response = await Axios.get('/folders.json')
        const folders = []

        Object.values(response.data).forEach((folder) => {
          folders.push(folder)
        })

        this.setState({
          folders
        })

      } catch (e) {
        console.log(e)
      }
    }

	render() {
		return (
			<div className="App">
				<div className='row'>
					<div className='col-2'>
            <Menu 
              folders={this.state.folders} 
              addFolder={this.addFolder}
              onDelete={this.deleteFolder}
              onSelectFolder={this.onSelectFolder}
            />
					</div>
					<div className='col-10'>
						<Main />
					</div>
				</div>
			</div>
		);
	}
}
