import React, {Component} from 'react'
import Checkbox from '../Checkbox/Checkbox'
import AddTask from '../AddTask/AddTask'
import './Card.scss'
import CardTitle from '../CardTitle/CardTitle';
import Axios from 'axios';

export default class Card extends Component {
    maxId = 100;

    state = {
        items: []
    }

    createTask(name) {
        return {
            name,
            important: false,
            done: false,
            id: this.maxId++
        }
    }

    addTask = (text) => {
        const newItem = this.createTask(text);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newItem
            ];

            return {
                items: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/test.json', newItem)
    };


    deleteItem = (id) => {
        this.setState(({ items }) => {
            const idx = items.findIndex((el) => el.id === id);
            const newArr = [
                ...items.slice(0, idx),
                ...items.slice(idx + 1)
            ];
            return {
                items: newArr
            }
        })
    };

    toggleProperty(arr, id, propName) {
        const idx = arr.findIndex((el) => el.id === id);

        const oldItem = arr[idx];
        const newItem = {...oldItem,
            [propName]: !oldItem[propName]};

        return [
            ...arr.slice(0, idx),
            newItem,
            ...arr.slice(idx + 1)
        ];
    }

    async componentDidMount() {
        try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/test.json')
            const items = []

            Object.values(response.data).forEach((key) => {
                items.push(this.createTask(key.name))
            })

            this.setState({
                items
            })

        } catch (e) {
            console.log(e)
        }
    }  

    onToggleImportant = (id) => {
        this.setState(({ items }) => {
            return {
                items: this.toggleProperty(items, id, 'important')
            }
        });
    };

    render() {
        return (
            <div className='TaskElement'>
                <CardTitle />
                {this.state.items.map((item) => {
                    return( 
                        <Checkbox
                            name={item.name}
                            id={item.id}
                            important={item.important}
                            done={item.done}
                            onToggleImportant={this.onToggleImportant}
                            onDeleted={ this.deleteItem }
                        />   
                    )                 
                })}
                <AddTask text='Добавить новую задачу' onItemAdded={this.props.addTask} />
            </div>
        )
    }
}
