// Чекбокс задач
import React from 'react'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import DeleteIcon from '@material-ui/icons/Delete';
import BookmarkIcon from '@material-ui/icons/Bookmark';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  label: {
    color: 'red',
  }
})

export default function Task({ name, keyx, important, onToggleImportant, onDeleted, onComplete, done }) {
  const classes = useStyles()
  return (
    <Grid
      container
      direction="row"
      justify="space-between"
      alignItems="center"
    >
      <Grid item>
        <FormControlLabel
          control={
            <Checkbox
              checked={done}
              id={keyx}
              onChange={() => onComplete(name, keyx)}
              color={important ? 'primary' : 'secondary'}
              inputProps={{ 'aria-label': 'primary checkbox' }}
            />
          }
          label={name}
          classes={important ? {label: classes.label}: ''}
        />
      </Grid>
      <Grid item>
        <IconButton 
          color="secondary"
          onClick={() => onDeleted(name, keyx)}
        >
          <DeleteIcon />
        </IconButton>
        <IconButton 
          color="primary"
          onClick={() => onToggleImportant(name, keyx)}
        >
          <BookmarkIcon />
        </IconButton>
      </Grid>
    </Grid>
  )
}
