import React, {Component} from 'react'
import Checkbox from '../UI/Task/Task'
import AddTask from '../UI/AddTask/AddTask'
import './Card.scss'
import CardTitle from './CardTitle/CardTitle';
import Axios from 'axios';

export default class Card extends Component {
  createTask(name) {
    return {
        name,
        important: false,
        done: false,
    }
  }

  // addTask = (text, cardKey) => {
  //   const newItem = this.createTask(text);
  //   this.setState(( this.state.folders.cards.cardKey ) => {
  //     const newArr = [
  //       ...items,
  //       newItem
  //     ];
  //     return {
  //       items: newArr
  //     }
  //   })
  //   // Axios.post(`/folders/${activeFolder}/cards/${cardKey}/tasks.json`, newItem)
  // }

  deleteTask = (id) => {
    this.setState(({ items }) => {
      const idx = items.findIndex((el) => el.id === id)
      const newArr = [
        ...items.slice(0, idx),
        ...items.slice(idx + 1)
      ]
      return {
        items: newArr
      }
    })
  }

  toggleProperty(arr, id, propName) {
    const idx = arr.findIndex((el) => el.id === id);

    const oldItem = arr[idx];
    const newItem = {...oldItem,
      [propName]: !oldItem[propName]}

    return [
      ...arr.slice(0, idx),
      newItem,
      ...arr.slice(idx + 1)
    ]
  }

  onToggleImportant = (id) => {
    this.setState(({ items }) => {
      return {
        items: this.toggleProperty(items, id, 'important')
      }
    })
  }
    async componentDidMount() {
        try {
            const response = await Axios.get(`https://tasks-8f93f.firebaseio.com/cards/${this.props.name}/tasks.json`)
            const items = []

            Object.values(response.data).forEach((task) => {
                items.push(this.createTask(task.name))
            })
            this.setState({
                items
            })

        } catch (e) {
            console.log(e)
        }
    }  



    render() {
        return (
            <div className='TaskElement'>
                <CardTitle />
                {/* {this.state.items.map((item) => {
                    return( 
                        <Checkbox
                            name={item.name}
                            id={item.id}
                            important={item.important}
                            done={item.done}
                            onToggleImportant={this.onToggleImportant}
                            onDeleted={ this.deleteTask }
                        />   
                    )                 
                })} */}
                <AddTask text='Добавить новую задачу' onItemAdded={this.addTask} />
            </div>
        )
    }
}
