// Чекбокс задач
import React from 'react'
import './Task.scss'

function Task({ name, keyx, important, onToggleImportant, onDeleted, onComplete, done }) {
    return (
        <div className='Checkbox'>
            <div className='label'>
                <input 
                    className='checkbox-custom' 
                    type="checkbox" 
                    id={keyx}
                    checked={done}
                    onClick={() => onChecked(name, keyx)}
                />
                <label 
                    className={important ? 'important' : ''}
                    for={keyx}
                >{name}</label>
            </div>
            <button 
                className="btn btn-outline-danger btn-checkbox"
                onClick={() => onDeleted(keyx)}
            >
                <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-trash-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5a.5.5 0 0 0-1 0v7a.5.5 0 0 0 1 0v-7z"/>
                </svg>
            </button>

            <button 
                className="btn btn-outline-primary btn-checkbox"
                onClick={() => onToggleImportant(name, keyx)}
            >
                <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-bookmark-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M2 2a2 2 0 0 1 2-2h8a2 2 0 0 1 2 2v13.5a.5.5 0 0 1-.74.439L8 13.069l-5.26 2.87A.5.5 0 0 1 2 15.5V2z"/>
                </svg>
            </button>
        </div>
    )
}

export default Task
