import React from 'react'
import './Checkbox.scss'


const Checkbox = props => {
    let textStyle = ''

    function readyTask() {
        textStyle = "color: 'red'"
        console.log(textStyle)
    }
    return (
        <div>
            <input 
                className='checkbox-custom' 
                type="checkbox" 
                id={props.id}
                onSubmit={readyTask()}
            />
            <label for={props.id} style={{textStyle}}>{props.task}</label>
        </div>
    )
}

export default Checkbox
