import React, { Component } from 'react';
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from 'axios'


export default class App extends Component {


	state = {
		folders: [],
		keys: []
	}

	createFolder(name) {
        return {
            name,
            id: this.maxId++
        }
    }


	async componentDidMount() {
		try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/folders.json')
            const folders = []
            const keys = []
            Object.keys(response.data).forEach((key) => {
                folders.push(this.createFolder())
                keys.push(key)
            })

            this.setState({
                folders
            })

            this.setState({
                keys
            })

        } catch (e) {
            console.log(e)
        }
	}

	render() {
		return (
			<div className="App">
				<div className='row'>
					<div className='col-2'>
						<Menu />
					</div>
					<div className='col-10'>
						<Main />
					</div>
				</div>
			</div>
		);

	}
}
