// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import TaskElement from '../../components/TaskElement/TaskElement'
import AddTaskElement from '../../components/AddTaskElement/AddTaskElement'
import Axios from 'axios'

export default class Main extends Component{
    maxId = 1000

    state = {
        cards: []
    }

    createCard(title, subtitle) {
        return {
            title,
            subtitle,
            id: this.maxId++,
            tasks: []
        }
    }

    addCard = (title, subtitle) => {
        const newCard = this.createCard(title, subtitle);
        this.setState(({ items }) => {
            const newArr = [
                ...items,
                newCard
            ];
            return {
                items: newArr
            }
        })
        Axios.post('https://tasks-8f93f.firebaseio.com/cards', newCard)
    };


    render() {
        return (
            <div className='Main'>
                <TaskElement />
                <AddTaskElement addCard={this.addCard} />
            </div>
        )
    }
}
