// Чекбокс задач
import React from 'react'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';


function Task({ name, keyx, important, onToggleImportant, onDeleted, onComplete, done }) {
    return (
        <div className='Checkbox'>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={done}
                      id={keyx}
                      onChange={() => onComplete(name, keyx)}
                      color={important ? 'primary' : 'secondary'}
                      inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                    }
                  label={name}
                />
            <Button 
                onClick={() => onDeleted(name, keyx)}
            >
            </Button>

            <Button 
                onClick={() => onToggleImportant(name, keyx)}
            >
            </Button>
        </div>
    )
}

export default Task
