import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from './axios/axios-folders'


export default class App extends Component { 
  state = {
    folders: [],
  }

  //

  createFolder(name) {
    return {
      name
    }
  }

  addFolder = (text) => {
    const newFolder = this.createFolder(text);
    Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    this.setState(({ folders }) => {
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  async componentDidMount() {
    try {      
      const responseFolders = await Axios.get('/folders.json')
      const folders = []
      Object.values(responseFolders.data).forEach((folder) => {
        folders.push(folder)
      })  
      this.setState({
        folders
      }) 

    } catch (e) {
      console.log(e)
    }
  }

  first = () => {
    return(
      <div className='container'>
        {/* <Menu 
          folders={this.state.folders} 
          addFolder={this.addFolder}
          deleteFolder={this.deleteFolder}
        /> */}
        <h1> Helllo </h1>
      </div>

    )
  }

  second = () => {
    return (
      <div className='row'>
        <div className='col-2'>
          <Menu 
            folders={this.state.folders} 
            addFolder={this.addFolder}
            deleteFolder={this.deleteFolder}
          />
        </div>
        <div className='col-10'>
          <Route path='/:id' render={({ match }) => <Main folderId={match.params.id} key={match.params.id} />} />
        </div>
      </div>
    )
  }

	render() {
		return (
        <Router>
          <Switch>
          <div className="App">

            <Route path='/' exact render={() => this.first}/>
            <Route path='/:id' render={() => this.second}/>
            </div>

          </Switch>
        </Router>
		);
	}
}
