// Контейнер для отображения элементов карточек с задачами
import React, {Component} from 'react'
import './Main.scss'
import Card from '../../components/Card/Card'
import AddCard from '../../components/UI/AddCard/AddCard'
import Axios from '../../axios/axios-folders'

export default class Main extends Component{
  state = {
    currentCards: []
  }

  createCard() {
    return {
        title: '',
        subtitle: '',
    }
  }

  addCard = async () => {
    console.log('Card 1')
    const activeFolder = this.props.folderId
    const newCard = this.createCard();
    await Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
    .then(function (res) {
      console.log('Card 2')
      const cardKey = ((Object.values(res.data))[0])
      newCard.key = cardKey
      Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
    })
    this.setState(({ currentCards }) => {
      console.log('Card 3')
      const newArr = [
        ...currentCards,
        newCard
      ];
      return {
        currentCards: newArr
      }
    })  
  }

  onDeleteCard = () => {
    const idx = this.state.tasks.findIndex((el) => el.name === name)
    const activeFolderKey = this.props.activeFolderKey
    const cardKey = this.props.keyx
    Axios.delete(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${key}.json`)
    this.setState(({ tasks }) => {
      const newArr = [
        ...tasks.slice(0, idx),
        ...tasks.slice(idx + 1)
      ]
      return {
        tasks: newArr
      }
    })
  }


  async componentDidMount() {
    try {  
      const activeFolder = this.props.folderId    
      const responseCards = await Axios.get(`/folders/${activeFolder}/cards.json`)
      const currentCards = []
      Object.values(responseCards.data).forEach((card) => {
        currentCards.push(card)
      })  
      this.setState({
        currentCards
      }) 

    } catch (e) {
      console.log(e)
    }
  }

  render() {
    return (
      <div className='Main'>
        {this.state.currentCards.map((card) => {
          return(
            <Card 
              key={card.key} 
              keyx={card.key} 
              activeFolderKey={this.props.folderId}
              title={card.title}
            />
          )
        })}
        <AddCard addCard={this.addCard} />
      </div>
    )
  }
}
