// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import './Menu.scss'
import AddTask from '../../components/UI/AddTask/AddTask'
import Folder from '../../components/UI/Folder/Folder'

export default class Menu extends Component {

  state = {
    folders: [],
  }

  //

  createFolder(name) {
    return {
      name
    }
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  addFolder = (text) => {
    const newFolder = this.createFolder(text);
    Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    this.setState(({ folders }) => {
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })
  }

  render() {
    return (
      <div className='Menu'>
        <ul className="list-group">
          {
            this.props.folders.map((item) => {
              return(
                <Folder 
                  title={item.name} 
                  keyx={item.key}  
                  key={item.key}        
                  onDelete={this.props.deleteItem}
                  onSelectFolder={this.props.onSelectFolder}
                />
              )
            })
          }
          <div className='addTask'>
            <AddTask 
              text='Новая папка' 
              onItemAdded={this.props.addFolder}
            />
          </div>
        </ul>
      </div>
    )    
  }
}
