// Контейнер для панели коллекции папок
import React, {Component} from 'react'
import './Menu.scss'
import AddTask from '../../components/AddTask/AddTask'
import MenuItem from '../../components/MenuItem/MenuItem'

export default class Menu extends Component {
    maxId = 1000

    state = {
        folders: [
            this.createFolder('Main Folder'),
            this.createFolder('Second Folder')
        ]
    }

    createFolder(value) {
        return {
            value,
            id: this.maxId++
        }
    }

    addItem = (text) => {
        const newItem = this.createFolder(text);
        this.setState(({ folders }) => {
            const newArr = [
                ...folders,
                newItem
            ];

            return {
                folders: newArr
            }
        })
    };

    deleteItem = (id) => {
        this.setState(({ folders }) => {
            const idx = items.findIndex((el) => el.id === id);
            const newArray = [
                ...items.slice(0, idx),
                ...items.slice(idx + 1)
            ];
            return {
                items: newArray
            }
        })
    };

    render() {
        return (
            <div className='Menu'>
                <ul className="list-group">
                    {
                        this.state.folders.map((item) => {
                            return(
                                <MenuItem title={item.value} id={item.id} />
                            )
                        })
                    }
                    <AddTask 
                        text='Новая папка' 
                        onItemAdded={this.addItem}
                        onDelete={this.deleteItem}
                    />
                </ul>
            </div>
        )    
    }
}
