import React, { Component } from 'react';
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from './axios/axios-folders'


export default class App extends Component { 
  state = {
    folders: [],
    currentCards: [],
    activeFolderKey: false
  }

  //

  addStarterPack = () => {
    this.addFolder('auto gen F')
  }


  createFolder(name) {
    return {
      name
    }
  }

  addFolder = (text) => {
    const newFolder = this.createFolder(text);
    Axios.post(`/folders.json`, newFolder)
    .then(function (res) {
      const folderKey = ((Object.values(res.data))[0])
      newFolder.key = folderKey
      Axios.patch(`/folders/${folderKey}.json`, {'key': folderKey})
    })
    this.setState(({ folders }) => {
      const newArr = [
        ...folders,
        newFolder
      ];
      return {
        folders: newArr
      }
    })
  }

  deleteFolder = (id) => {
    this.setState(({ folders }) => {
      const idx = folders.findIndex((el) => el.id === id);
      const newArray = [
        ...folders.slice(0, idx),
        ...folders.slice(idx + 1)
      ]
      return {
        folders: newArray
      }
    })
  }

  onSelectFolder = async (key) => {
    this.setState({
      currentCards: []
    })
    this.setState({
      activeFolderKey: key
    })
    try {
      const response = await Axios.get(`/folders/${this.state.activeFolderKey}/cards.json`)
      const currentCards = []
      Object.values(response.data).forEach((card) => {
        currentCards.push(card)
      })
      this.setState({
        currentCards
      })
    } catch (e) {
        console.log(e)
    }
  }

  //

  createCard() {
    return {
        title: '',
        subtitle: '',
    }
  }

  addCard = () => {
    const newCard = this.createCard();
    const activeFolder = this.state.activeFolderKey
    Axios.post(`/folders/${activeFolder}/cards.json`, newCard)
    .then(function (res) {
      const cardKey = ((Object.values(res.data))[0])
      newCard.key = cardKey
      Axios.patch(`/folders/${activeFolder}/cards/${cardKey}.json`, {'key': cardKey})
    })
    this.setState(({ currentCards }) => {
      const newArr = [
        ...currentCards,
        newCard
      ];
      return {
        currentCards: newArr
      }
    })
  }

  deleteCard = (id) => {
    console.log(id)
  }

  //



  //

  async componentDidMount() {
    try {      
      const responseFolders = await Axios.get('/folders.json')
      const folders = []

      Object.values(responseFolders.data).forEach((folder) => {
        folders.push(folder)
      })

      console.log('sdaaf')

      this.setState({
        folders
      })   

    } catch (e) {
      console.log(e)
    }
  }

	render() {
		return (
			<div className="App">
				<div className='row'>
					<div className='col-2'>
            <Menu 
              folders={this.state.folders} 
              addFolder={this.addFolder}
              deleteFolder={this.deleteFolder}
              onSelectFolder={this.onSelectFolder}
            />
					</div>
					<div className='col-10'>
            {
              this.state.activeFolderKey ?
              <Main 
                addCard={this.addCard}
                cards={this.state.currentCards}
                deleteCard={this.deleteCard}
                addTask={this.addTask}
                deleteTask={this.deleteTask}
                onToggleImportant={this.onToggleImportant}
                activeFolderKey={this.state.activeFolderKey}
              /> :
              <h1>Select Directory</h1>
            }
					</div>
				</div>
			</div>
		);
	}
}
