import React from 'react'
import './Checkbox.scss'


const Checkbox = props => {
    const textStyle = ''

    function readyTask() {
        textStyle = color= 'red'
    }
    return (
        <div>
            <input 
                className='checkbox-custom' 
                type="checkbox" 
                id={props.id}
                onClick={readyTask}
                style={textStyle}
            />
            <label for={props.id}>{props.task}</label>
        </div>
    )
}

export default Checkbox
