import React, {Component} from 'react'

export default class CardTitle extends Component {
    state = {
        title: '',
        subtitle: '',
        showTitle: false,
    }

    onTitleChange = (e) => {
        this.setState({
            title: e.target.value
        })
    };
    onSubtitleChange = (e) => {
        this.setState({
            subtitle: e.target.value
        })
    };

    onSubmit = (e) => {
        e.preventDefault()
        this.setState({
            showTitle: !this.state.showTitle
        })
    }

    FormInput = () => {
      return (
        <form
            onSubmit={this.onSubmit}
        >
            <button className="btn" type="submit" id="button-addon1" />
              <input 
                  type='text' 
                  placeholder='Заголовок' 
                  className='h1'
                  onChange={this.onTitleChange}   
                  value={this.state.title} 
              />
        </form>
      )
    }

    Title = () => {
        return (
            <div>
                <h1 onDoubleClick={this.onSubmit}>{this.state.title}</h1>
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.state.showTitle ? <this.Title /> : <this.FormInput />}
            </div>
        )
    }
}
