import React, {Component} from 'react'
import './Checkbox.scss'


class Checkbox extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            completeStyle :  {textDecoration: 'none'}
        }
    }
    changeTask() {
        this.setState({ 
            completeStyle : {textDecoration: 'line-through', opacity: .3}
        })
    }
    render() {
        return (
            <div>
                <input 
                    className='checkbox-custom' 
                    type="checkbox" 
                    id={this.props.id}
                    onChange={() => this.changeTask()}
                />
                <label 
                    for={this.props.id} 
                    style={ this.state.completeStyle } 
                >{this.props.task}</label>
            </div>
        )

    }
}

export default Checkbox
