import React from 'react'
import './Checkbox.scss'


class Checkbox extends React.Component {
    constructor(props) {
        super(props)
        this.state= {
            styl : "color:'green"
        }
    }
    render() {
        return (
            <div>
                <input 
                    className='checkbox-custom' 
                    type="checkbox" 
                    id={this.props.id}
                />
                <label for={this.props.id}>{this.props.task}{this.state.styl}</label>
            </div>
        )

    }
}

export default Checkbox
