import React, {Component} from 'react'
import Task from '../UI/Task/Task'
import AddTask from '../UI/AddTask/AddTask'
import './Card.scss'
import CardTitle from './CardTitle/CardTitle';
import Axios from '../../axios/axios-folders';

export default class Card extends Component {
  state = {
    tasks: []
  }

  createTask(name) {
    return {
        name,
        important: false,
        done: false,
    }
  }

  addTask = async (text) => {
    const newTask = this.createTask(text);
    const activeFolderKey = this.props.activeFolderKey
    const cardKey = this.props.keyx
    await Axios.post(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`, newTask)
    .then(function (res) {
      const taskKey = ((Object.values(res.data))[0])
      newTask.key = taskKey
      Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'key': taskKey})
    })
    this.setState(({ tasks }) => {
      const newArr = [
        ...tasks,
        newTask
      ];
      return {
        tasks: newArr
      }
    })
  }

  deleteTask = (id) => {
    this.setState(({ tasks }) => {
      const idx = tasks.findIndex((el) => el.id === id)
      const newArr = [
        ...tasks.slice(0, idx),
        ...tasks.slice(idx + 1)
      ]
      return {
        tasks: newArr
      }
    })
  }

  toggleProperty(arr, id, propName) {
    const idx = arr.findIndex((el) => el.id === id);

    const oldItem = arr[idx];
    const newItem = {...oldItem,
      [propName]: !oldItem[propName]}

    return [
      ...arr.slice(0, idx),
      newItem,
      ...arr.slice(idx + 1)
    ]
  }

  onToggleImportant = (id) => {
    this.setState(({ items }) => {
      return {
        items: this.toggleProperty(items, id, 'important')
      }
    })
  }

    async componentDidMount() {
      const cardKey = this.props.keyx
      const activeFolderKey = this.props.activeFolderKey
        try {
            const response = await Axios.get(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`)
            const tasks = []

            Object.values(response.data).forEach((task) => {
                tasks.push(this.createTask(task.name))
            })
            this.setState({
                tasks
            })

        } catch (e) {
            console.log(e)
        }
    }  



    render() {
        return (
            <div className='TaskElement'>
                <CardTitle 
                  keys={this.props.keyx} />
                {this.state.tasks.map((item) => {
                    return( 
                        <Task
                            name={item.name}
                            important={item.important}
                            done={item.done}
                            onToggleImportant={this.onToggleImportant}
                            onDeleted={ this.deleteTask }
                            key={item.key}
                            keyx={itemkey}
                        />   
                    )                 
                })}
                <AddTask text='Добавить новую задачу' onItemAdded={this.addTask} />
            </div>
        )
    }
}
