import React, { Component } from 'react';
import './App.scss';
import Menu from './containers/Menu/Menu.js'
import Main from './containers/Main/Main.js'
import Axios from 'axios'


export default class App extends Component {
	maxId = 1000


	state = {
		folders: [],
		keys: []
	}

	createFolder(name) {
        return {
            name,
            id: this.maxId++
        }
    }

	addFolder = (text) => {
        const newFolder = this.createFolder(text);
        this.setState(({ folders }) => {
            const newArr = [
                ...folders,
                newFolder
            ];
            return {
                folders: newArr
            }
        })
        Axios.post(`https://tasks-8f93f.firebaseio.com/folders.json`, newFolder)
    };


	async componentDidMount() {
		try {
            const response = await Axios.get('https://tasks-8f93f.firebaseio.com/folders.json')
            const folders = []
            const keys = []
            Object.keys(response.data).forEach((folder) => {
                folders.push(this.createFolder(folder.name))
                keys.push(folder)
            })

            this.setState({
                folders
            })

            this.setState({
                keys
            })

        } catch (e) {
            console.log(e)
        }
	}

	render() {
		return (
			<div className="App">
				<div className='row'>
					<div className='col-2'>
						<Menu 
						folders={this.state.keys} />
					</div>
					<div className='col-10'>
						<Main cards={this.state.folders.cards} />
					</div>
				</div>
			</div>
		);

	}
}
