import React, { useState } from 'react'
import axios from 'axios'
import './AddTask.scss'

function AddTask({ onCreate }) {
    const [value, setValue] = useState('')
    function submitHandler(event) {
        event.preventDefault()
        axios.post('https://proj2-181ce.firebaseio.com/todo.json', event.target.value)
        if (value.trim()) {
            onCreate(value)
            setValue('')
        }
    }
    return (
        <form className="input-group mb-3" onSubmit={submitHandler}>
            <div className="input-group-prepend">
                <button className="btn" type="submit" id="button-addon1">+</button>
            </div>
            <input 
                type="text" 
                className="form-control" 
                placeholder="New Task" 
                aria-label="Example text with button addon" 
                aria-describedby="button-addon1" 
                value={value} 
                onChange={event => setValue(event.target.value)} 
            />
        </form>

    )
}

export default AddTask
