import React, {Component} from 'react'
import Task from '../UI/Task/Task'
import AddTask from '../UI/AddTask/AddTask'
import './Card.scss'
import CardTitle from './CardTitle/CardTitle';
import Axios from '../../axios/axios-folders';

export default class Card extends Component {
  state = {
    tasks: []
  }

  createTask(name) {
    return {
        name,
        important: false,
        done: false,
        key: ''
    }
  }

  addTask = async (text) => {
    const newTask = this.createTask(text);
    const activeFolderKey = this.props.activeFolderKey
    const cardKey = this.props.keyx
    await Axios.post(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`, newTask)
    .then(function (res) {
      const taskKey = ((Object.values(res.data))[0])
      newTask.key = taskKey
      Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'key': taskKey})
    })
    this.setState(({ tasks }) => {
      const newArr = [
        ...tasks,
        newTask
      ];
      return {
        tasks: newArr
      }
    })
  }

  deleteTask = (id) => {
    const idx = tasks.findIndex((el) => el.id === id)
    const oldItem = this.state.tasks[idx];
    Axios.delete(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${key}.json`)
    this.setState(({ tasks }) => {
      const newArr = [
        ...tasks.slice(0, idx),
        ...tasks.slice(idx + 1)
      ]
      return {
        tasks: newArr
      }
    })
  }

  toggleProperty(arr, name, propName) {
    const idx = arr.findIndex((el) => el.name === name);

    const oldItem = arr[idx];
    const newItem = {...oldItem,
      [propName]: !oldItem[propName]}

    return [
      ...arr.slice(0, idx),
      newItem,
      ...arr.slice(idx + 1)
    ]
  }

  onToggleImportant = (name, key) => {
    const idx = this.state.tasks.findIndex((el) => el.name === name);
    const oldItem = this.state.tasks[idx];
    const newItem = {...oldItem,
      ['important']: !oldItem['important']
    }
    const activeFolderKey = this.props.activeFolderKey
    const cardKey = this.props.keyx
    const taskKey = key
    Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'important': newItem.important})
    this.setState(({ tasks }) => {
      return {
        tasks: this.toggleProperty(tasks, name, 'important')
      }
    })
  }

  onComplete = (name, key) => {
    const idx = this.state.tasks.findIndex((el) => el.name === name);
    const oldItem = this.state.tasks[idx];
    const newItem = {...oldItem,
      ['done']: !oldItem['done']
    }
    const activeFolderKey = this.props.activeFolderKey
    const cardKey = this.props.keyx
    const taskKey = key
    Axios.patch(`/folders/${activeFolderKey}/cards/${cardKey}/tasks/${taskKey}.json`, {'done': newItem.done})
    this.setState(({ tasks }) => {
      return {
        tasks: this.toggleProperty(tasks, name, 'done')
      }
    })
  }

  async componentDidMount() {
    try {  
      const cardKey = this.props.keyx
      const activeFolderKey = this.props.activeFolderKey
      const responseTasks = await Axios.get(`/folders/${activeFolderKey}/cards/${cardKey}/tasks.json`)
      const tasks = []
      Object.values(responseTasks.data).forEach((task) => {
        tasks.push(task)
      })  

      this.setState({
        tasks
      }) 

    } catch (e) {
      console.log(e)
    }
  }  

    render() {
        return (
            <div className='TaskElement'>
                <CardTitle />
                {this.state.tasks.map((task) => {
                  return( 
                    <Task
                      name={task.name}
                      important={task.important}
                      done={task.done}
                      key={task.key}
                      keyx={task.key}
                      onToggleImportant={this.onToggleImportant}
                      onDeleted={ this.deleteTask }
                      onComplete={this.onComplete}
                    />   
                  )                 
                })}
                <AddTask text='Добавить новую задачу' onItemAdded={this.addTask} />
            </div>
        )
    }
}
