import React, {Component} from 'react'
import './CardTitle.scss'

export default class CardTitle extends Component {
    state = {
        text: '',
        showTitle: false,
    }

    onTitleChange = (e) => {
        this.setState({
            text: e.target.value
        })
    };


    onSubmit = (e) => {
        e.preventDefault()
        this.setState({
            showTitle: !this.state.showTitle
        })
    }

    FormInput = () => {
        return (
            <form
                onSubmit={this.onSubmit}
            >
                <button className="btn" type="submit" id="button-addon1" />
                    <input 
                        type='text' 
                        placeholder='Заголовок' 
                        className='h1'
                        onChange={this.onTitleChange}    
                    />
                    <input 
                        type='text' 
                        placeholder='Описание' 
                        className='h5'
                        onChange={this.onTitleChange}    
                    />
            </form>
        )
    }

    Title = () => {
        return (
            <div>
                <h1 onClick={this.onSubmit} >{this.state.text}</h1>
                <h5 onClick={this.onSubmit} >{this.state.text}</h5>
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.state.showTitle ? <this.Title /> : <this.FormInput />}
            </div>
        )
    }
}
